      .container-reading {
  
  
        display: flex;
        flex-direction: column;
        align-items: center;
        width: 100%;
        margin-left: 50px;
        padding-left: 20px;
       
  
      }
    
    
      .profile-area {
        
        display: flex;
        flex-direction: column;
        align-items: center;
        border-left: 3px solid #ccc;
        border-right:3px solid #ccc; ;
        width: 13%;
        padding-right: 1px;
  
        background: linear-gradient(180deg, #6ed5cc, #479aaf, rgba(59,85,109,1))
  
      }
      .avatar {
      border-radius: 50%;
      height: 64px;
      object-fit: cover;
      object-position: center;
      width: 64px;
  }
      .profile-area img {
        margin-top: 3rem;
        margin-bottom: 1rem;
        border-radius: 50%;
        width: 100px;
        height: 100px;
        object-fit: cover;
     
      }
    
    .profile-username {
      font-size: x-large;
      font-weight: bolder;
      }
  
      .card {
        display: flex;
        align-items: center;
        margin: 2rem 0;
        padding: 1rem;
        border: 1px solid #ccc;
        border-radius: 10px;
        max-width: 600px;
        background: radial-gradient(circle at 50% 50%,rgba(251, 255, 255, 1), rgba(234, 251, 255, 1));
      }
      button{
        display: flex;
        justify-content:left;
       
  
      }
      .card img {
        margin-right: 1rem;
        width: 150px;
        height: 150px;
        object-fit: cover;
      }
    
      .card .container {
        display: flex;
        flex-direction: column;
      }
    
      .card h4 {
        margin: 0;
      }
    
      .card .auteur {
        margin-right: 0.5rem;
      }
    
      .card p {
        margin: 0.5rem 0;
      }
    
      .card .description {
        margin-top: 0.5rem;
      }
    
      .auteur {
        font-weight: bold;
        margin-right: 5px;
        display: inline-block;
      }
      a{
        font-size: larger;
        font-weight: bold;
        margin-top: 20px;
      }
      a:hover{
        color:rgb(5, 109, 81);
      }
     /*Animation pour h1*/
  
      .animate-charcter{
      font-family: "Raleway", sans serif;
      margin-top:25px;
      text-transform: uppercase;
      background-image: linear-gradient(
        -225deg,
        #0B162C 0%,
        #1C2942 29%,
        #3B556D 67%,
        #5FC2BA 100%
